import React from 'react'
import API from "../../utils/API";

class SearchBookForm extends React.Component {
    state = {
        result: [],
        search: ""
      };


      componentDidMount() {
        console.log("API mount")
        this.searchTitle("the universe");
      }

      makeBook = bookData => {
        return {
            _id: bookData.id,
            title: bookData.volumeInfo.title,
            authors: bookData.volumeInfo.authors,
            description: bookData.volumeInfo.description,
            image: bookData.volumeInfo.imageLinks.thumbnail,
            link: bookData.volumeInfo.previewLink
        }
    }

      searchTitle = query => {
        API.getBook(query)
        .then(res => this.setState({ result: res.data.items.map(bookData => this.makeBook(bookData)) }))
        .catch(err => console.error(err));
          console.log(this.state.result)
      };


      handleOnChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };

    handleOnSubmit = event => {
        event.preventDefault();
        this.searchBook(this.state.search);
    };
    render() {
        return (
        <div style = {formStyle} className = "form-container">
            <label htmlFor="search">Search by Title</label>
            <input
            onChange={this.handleOnChange}
            value={this.state.search}
            name = "search"
            type="text"
            className="form-control"
            placeholder = "Search by title..."
            id="search">
            </input>
            <button onClick={this.handleOnSubmit} type="submit" className="btn btn-primary">Submit</button>
        </div>
    )
    }

}

const formStyle = {
    border: "1px solid black",
    padding: "10px"
}



export default SearchBookForm;
