import React, { Component } from 'react'
import SearchBookForm from "../components/Form/SearchBookForm";
import AllBooks from "../components/Books/AllBooks";
import API from "../utils/API";

export class SearchBook extends Component {
    state = {
        result: [],
        search: ""
      };


      componentDidMount() {
        console.log("API mount")
        this.searchBook("The Universe");
      }

      makeBook = bookData => {
        return {
            _id: bookData.id,
            title: bookData.volumeInfo.title,
            authors: bookData.volumeInfo.authors,
            description: bookData.volumeInfo.description,
            image: bookData.volumeInfo.imageLinks.thumbnail,
            link: bookData.volumeInfo.previewLink
        }
    }

      searchBook = query => {
        API.getBook(query)
        .then(res => this.setState({ result: res.data.items.map(bookData => this.makeBook(bookData)) }))
        .catch(err => console.error(err));
          console.log(this.state.result)
      };


      handleOnChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
    };

    handleOnSubmit = event => {
        event.preventDefault();
        this.searchBook(this.state.search);
    };

    render() {
        return (
                <div style = {containerStyle} className = "container">
                    <div className = "row">
                        <div className = "col-md-3">
                        <SearchBookForm
                            value = {this.state.search}
                            handleOnChange={this.handleOnChange}
                            handleOnSubmit={this.handleOnSubmit}
                        />
                        </div> 
                        <div className = "col-md-9">
                            {this.state.result.map((results) => (
                            <AllBooks
                                // key = {results.id}
                                // title={results.title}
                                // author={results.authors}
                                // description={results.Director}
                                // image={results.imageLinks.thumbnail}
                                // link={results.Released}
                                />
                            ))}
                        </div>
                    </div>
                </div>
        )
    }
}

const containerStyle = {
    border: "1px solid black",
    padding: "20px",
    marginTop: "40px"
  }

export default SearchBook
