import React from 'react'

function AllBooks(props) {
    return (
        <div style = {booksStyle} className = "all-books">
            <h4>{props.title}</h4>
            <p>Author: {props.author.map((authors) => (
                authors[i] > 1 ? props.author[0] + " & " + props.author[1] : props.author
            ))}
            </p>
            <img src = {props.image} alt = "book" />
        </div>


    )
}


const booksStyle = {
    border: "1px solid black",
    marginBottom: "5px",
    padding: "10px",
    textAlign: "center"
}



export default AllBooks;
