import React from 'react'

function Form(props) {
    return (
        <div>
            <div style = {formStyle} className="form">
                <label style = {{margin: "0"}} htmlFor="search"><p>Search for a Book!</p></label>
                <input
                    onChange={props.handleInputChange}
                    value={props.search}
                    name="search"
                    type="text"
                    className="form-control"
                    placeholder="Search by Title..."
                    id="search"
                />
                <button style = {{marginTop: "10px"}} onClick={props.handleFormSubmit} className="btn btn-primary">Search</button>
            </div>
        </div>
    )
}

const formStyle = {
    border: "1px solid black",
    borderRadius: "5px",
    backgroundColor: "rgb(133, 186, 232)",
    padding: "10px",
    marginTop: "20px"
}
export default Form;
